# Add Report indexes
docker exec mongodb-report mongo --username root --password "$1" --eval "db = db.getSiblingDB('openrmfreport'); db.ACASScanReport.createIndex({ created: -1 }); db.ACASScanReport.createIndex({ updatedOn: -1 }); db.ACASScanReport.createIndex({ operatingSystem: 1 }); db.ACASScanReport.createIndex({ family: 1 }); db.ACASScanReport.createIndex({ systemGroupId: 1 }); db.VulnerabilityReport.createIndex({ created: -1 }); db.VulnerabilityReport.createIndex({ updatedOn: -1 }); db.VulnerabilityReport.createIndex({ severityOverride: 1 }); db.VulnerabilityReport.createIndex({ systemGroupId: 1 }); db.VulnerabilityReport.createIndex({ artifactId: 1 }); db.VulnerabilityReport.createIndex({ severity: 1 });"